function localization_menu(langage) { var filename; switch (langage) { case "en": case "0": filename = "localization_en.xml"; break; case "fr": case "1": filename = "localization_fr.xml"; break; case "sp": case "2": filename = "localization_es.xml"; break; case "zh": case "3": filename = "localization_zh.xml"; break; case "sv": case "4": filename = "localization_sv.xml"; break; case "nl": case "5": filename = "localization_nl.xml"; break; case "it": case "6": filename = "localization_it.xml"; break; case "de": case "7": filename = "localization_de.xml"; break; case "cs": case "8": filename = "localization_cs.xml"; break; case "sk": case "9": filename = "localization_sk.xml"; break; case "pt": case "10": filename = "localization_pt.xml"; break; case "ru": case "11": filename = "localization_ru.xml"; break; case "no": case "12": filename = "localization_no.xml"; break; case "da": case "13": filename = "localization_da.xml"; break; default: filename = "localization_en.xml"; break; } return filename; }